import React from "react";
import Link from "next/link";

// packages
import ReactMarkdown from "react-markdown";
import Moment from "react-moment";

// api
import { getArticles, getArticle, getCategories } from "../../lib/api";

// components
import Layout from "../../components/layout";

// material ui
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Divider from "@material-ui/core/Divider";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Grid from "@material-ui/core/Grid";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
//import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";

import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: "90px"
  },
  media: {
    height: 300
  },
  card: {
    margin: "1.5rem 0 0"
  }
}));

function ListItemLink(props) {
  return <ListItem button component="a" {...props} />;
}

const Article = ({ article, categories }) => {
  const classes = useStyles();

  const imageUrl = article.image.url.startsWith("/")
    ? process.env.API_URL + article.image.url
    : article.image.url;

  console.log(categories);

  return (
    <Layout categories={categories}>
      <Grid container spacing={3}>
        <Grid item xs={12} md={7}>
          <Card className={classes.root}>
            <CardMedia
              component="img"
              image={imageUrl}
              className={classes.media}
            />
            <CardContent className={classes.card}>
              <Typography variant="h3">{article.title}</Typography>
              <Box m={4} />

              <Typography variant="body2" color="textSecondary" component="p">
                <Box fontStyle="italic" component="span">
                  <Moment format="MMM Do YYYY">{article.published_at}</Moment>
                </Box>{" "}
                |{" "}
                <Box fontWeight="fontWeightBold" component="span">
                  {article.category.name}
                </Box>
              </Typography>
              <Box m={2} />
              <Divider light={true} />
              <Box fontFamily="fontFamily" lineHeight={1.6}>
                <ReactMarkdown source={article.content} />
              </Box>
            </CardContent>
            <CardActions>
              <Link href="/">
                <Button>Back</Button>
              </Link>
            </CardActions>
          </Card>
        </Grid>
        <Grid item xs={12} md={5}>
          <List component="nav" aria-label="secondary mailbox folders">
            <ListItem button>
              <ListItemText primary="Trash" />
            </ListItem>
          </List>
          {categories.map((category) => {
            return (
              <ListItemLink
                as={`/category/${category.id}`}
                href="/category/[id]"
              >
                <ListItemText primary={categories.name} />
              </ListItemLink>
            );
          })}
        </Grid>
      </Grid>
    </Layout>
  );
};

export async function getStaticPaths() {
  const articles = (await getArticles()) || [];
  return {
    paths: articles.map((article) => ({
      params: {
        id: article.id
      }
    })),
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  const article = (await getArticle(params.id)) || [];
  const categories = (await getCategories()) || [];
  return {
    props: { article, categories },
    unstable_revalidate: 1
  };
}

export default Article;
