import Head from "next/head";
import Nav from "./nav";

import { Container, Row, Col } from "shards-react";

const Layout = ({ children, categories }) => (
  <>
    <Head>
      <title>Tony G. - Devtones</title>
    </Head>
    <Container fluid="true">
      <Row>
        <Col>
          <Nav categories={categories} />
          {children}
        </Col>
      </Row>
    </Container>
  </>
);

export default Layout;
