import React from "react";
import Link from "next/link";

// packages
import ReactMarkdown from "react-markdown";
import Moment from "react-moment";

// api
import { getArticles, getArticle, getCategories } from "../../lib/api";

// components
import Layout from "../../components/layout";
import Title from "../../components/title";

// material ui
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Divider from "@material-ui/core/Divider";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 300
  },
  card: {
    margin: "1.5rem 0 0"
  }
}));

const Article = ({ article, categories }) => {
  const classes = useStyles();

  const imageUrl = article.image.url.startsWith("/")
    ? process.env.API_URL + article.image.url
    : article.image.url;

  return (
    <Layout categories={categories}>
      <Title title={article.title} />
      <Grid container spacing={3} className={classes.root}>
        <Grid item xs={12} md={10}>
          <Card>
            <CardMedia
              component="img"
              image={imageUrl}
              className={classes.media}
            />
            <CardContent className={classes.card}>
              <Typography variant="body2" color="textSecondary" component="p">
                <Box fontStyle="italic" component="span">
                  <Moment format="MMM Do YYYY">{article.published_at}</Moment>
                </Box>{" "}
                |{" "}
                <Box fontWeight="fontWeightBold" component="span">
                  {article.category.name}
                </Box>
              </Typography>
              <Box m={2} />
              <Divider light={true} />
              <Box fontFamily="fontFamily" lineHeight={1.6}>
                <ReactMarkdown source={article.content} />
              </Box>
            </CardContent>
            <CardActions>
              <Link href="/">
                <Button>Back</Button>
              </Link>
            </CardActions>
          </Card>
        </Grid>
        <Grid item xs={12} md={2}>
          <Typography variant="overline" color="textSecondary" component="p">
            Other posts
          </Typography>
          <Divider light={true} />
          <Box m={1} />
          {recentArticles.map((article) => {
            return (
              <MenuItem key={article.id}>
                <Link as={`/article/${article.id}`} href="/article/[id]">
                  <Typography variant="button" component="p">
                    {article.title.slice(0, 14)}...
                  </Typography>
                </Link>
              </MenuItem>
            );
          })}
          <Box m={4} />
          <Typography variant="overline" color="textSecondary" component="p">
            Topics
          </Typography>
          <Divider light={true} />
          <Box m={1} />
          {categories.map((category) => {
            return (
              <MenuItem key={category.id}>
                <Link as={`/category/${category.id}`} href="/category/[id]">
                  <Typography variant="button" component="p">
                    {category.name}
                  </Typography>
                </Link>
              </MenuItem>
            );
          })}
        </Grid>
      </Grid>
    </Layout>
  );
};

export async function getStaticPaths() {
  const articles = (await getArticles()) || [];
  return {
    paths: articles.map((article) => ({
      params: {
        id: article.id
      }
    })),
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  const article = (await getArticle(params.id)) || [];
  const categories = (await getCategories()) || [];
  return {
    props: { article, categories },
    unstable_revalidate: 1
  };
}

export default Article;
