import React from "react";
import Link from "next/link";

// api
import { getCategory, getCategories, getArticles } from "../../lib/api";

// components
import Layout from "../../components/layout";
import Articles from "../../components/articles";
import Title from "../../components/title";

// material ui
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import MenuItem from "@material-ui/core/MenuItem";
import Typography from "@material-ui/core/Typography";
import { Divider } from "@material-ui/core";

const Category = ({ category, categories, articles }) => {
  const recentArticles = articles.reverse().slice(0, 5);

  return (
    <Layout categories={categories}>
      <Title title={category.name} />
      <Grid container spacing={3}>
        <Grid item xs={12} md={10}>
          <Articles articles={category.articles} />
        </Grid>
        <Grid item xs={12} md={2}>
          <Typography variant="overline" color="textSecondary" component="p">
            Topics
          </Typography>
          <Divider light={true} />
          <Box m={1} />
          {categories.map((category) => {
            return (
              <MenuItem key={category.id}>
                <Link as={`/category/${category.id}`} href="/category/[id]">
                  <Typography variant="button" component="p">
                    {category.name}
                  </Typography>
                </Link>
              </MenuItem>
            );
          })}
          <Box m={3} />
          <Typography variant="overline" color="textSecondary" component="p">
            Other posts
          </Typography>
          <Divider light={true} />
          <Box m={1} />
          {recentArticles.map((article) => {
            return (
              <MenuItem key={article.id}>
                <Link as={`/article/${article.id}`} href="/article/[id]">
                  <Typography variant="button" component="p">
                    {article.title}
                  </Typography>
                </Link>
              </MenuItem>
            );
          })}
        </Grid>
      </Grid>
    </Layout>
  );
};

export async function getStaticPaths() {
  const categories = (await getCategories()) || [];
  return {
    paths: categories.map((category) => ({
      params: {
        id: category.id
      }
    })),
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  const articles = (await getArticles()) || [];
  const category = (await getCategory(params.id)) || [];
  const categories = (await getCategories()) || [];
  return {
    props: { articles, category, categories },
    unstable_revalidate: 1
  };
}

export default Category;
