import React from 'react'
import Link from 'next/link'

// material ui
import { makeStyles } from '@material-ui/core/styles'
import AppBar from '@material-ui/core/AppBar'
import Toolbar from '@material-ui/core/Toolbar'
import Typography from '@material-ui/core/Typography'
import Button from '@material-ui/core/Button'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'

import InstagramIcon from '@material-ui/icons/Instagram'
import MailOutlineOutlinedIcon from '@material-ui/icons/MailOutlineOutlined'

import { ThemeProvider, createMuiTheme } from '@material-ui/core/styles'

const theme = createMuiTheme({
  typography: {
    fontFamily: ['Poppins', 'sans-serif'].join(',')
  }
})

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    marginBottom: theme.spacing(8)
  },
  menuButton: {
    marginRight: theme.spacing(1)
  },
  socialButton: {
    marginRight: theme.spacing(1),
    color: '#1a1a1a',
    opacity: 0.8
  },
  title: {
    flexGrow: 1
  }
}))

export default function Nav({ categories }) {
  const classes = useStyles()
  const [anchorEl, setAnchorEl] = React.useState(null)

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget)
  }

  const handleClose = () => {
    setAnchorEl(null)
  }

  return (
    <ThemeProvider theme={theme}>
      <div className={classes.root}>
        <AppBar color="transparent" elevation={0}>
          <Toolbar className="toolBar">
            <div>
              <a href="https://amazon.com" className={classes.socialButton}>
                <InstagramIcon fontSize="large" />
              </a>
              <a href="https://google.com" className={classes.socialButton}>
                <MailOutlineOutlinedIcon fontSize="large" />
              </a>
            </div>

            <div>
              <Link href="/">
                <Button className={classes.menuButton}>Home</Button>
              </Link>
              <Link href="/about">
                <Button className={classes.menuButton}>About</Button>
              </Link>
              <Button
                aria-controls="simple-menu"
                aria-haspopup="true"
                onClick={handleClick}
              >
                ⚍ Topics
              </Button>
              <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
              >
                {categories.map((category) => {
                  return (
                    <MenuItem key={category.id} onClick={handleClose}>
                      <Link
                        as={`/category/${category.id}`}
                        href="/category/[id]"
                      >
                        <Typography variant="button" component="p">
                          {category.name}
                        </Typography>
                      </Link>
                    </MenuItem>
                  )
                })}
              </Menu>
            </div>
          </Toolbar>
        </AppBar>
      </div>
    </ThemeProvider>
  )
}
