import React from "react";
import Template from "./template";

import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Icon from "@material-ui/core/Icon";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1
  }
}));

const Articles = ({ articles }) => {
  const classes = useStyles();

  const newArticles = articles.reverse();
  console.log(newArticles);

  const leftArticlesCount = Math.ceil(newArticles.length / 3);
  const leftArticles = newArticles.slice(0, leftArticlesCount);
  const rightArticles = newArticles.slice(
    leftArticlesCount,
    newArticles.length
  );

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={12} md={7}>
          {leftArticles.map((article) => {
            return (
              <Template article={article} key={`article__${article.id}`} />
            );
          })}
        </Grid>
        <Grid item xs={12} md={5}>
          {rightArticles.map((article) => {
            return (
              <Template article={article} key={`article__${article.id}`} />
            );
          })}
        </Grid>
      </Grid>
    </div>
  );
};

export default Articles;
